<script>
import { Grid, GridItem } from "ecommerce-ui-nuggets";
import { mask } from "vue-the-mask";
export default {
  name: "CreditCard",
  components: {
    Grid,
    GridItem
  },
  directives: {
    mask
  },
  data() {
    return {
      cardType: "",
      cardLength: 19,
      cardMaskPattern: "#### #### #### ####",
      cvvLength: 3,
      cardImg: "",
      expirationLength: 5,
      expirationDate: ""
    };
  },
  methods: {
    validateCard: function(cardNumber) {
      const visa_regex = /^4[0-9]{12}(?:[0-9]{3})?$/; // Number starts with 4
      const master_card_regex = /^(?:5[1-5][0-9]{2}|222[1-9]|22[3-9][0-9]|2[3-6][0-9]{2}|27[01][0-9]|2720)[0-9]{12}$/; // Number starts with 51 through 55 or with the numbers 2221 through 2720
      const amex_regex = /^3[47][0-9]{13}$/; // Number starts with 34 or 37
      const diners_regex = /^3(?:0[0-5]|[68][0-9])[0-9]{11}$/; // Number starts with 300 through 305, 36 or 38
      const discover_regex = /^6(?:011|5[0-9]{2})[0-9]{12}$/; // Number starts with 6011 or 65
      const jcb_regex = /^(?:2131|1800|35\d{3})\d{11}$/; // JCB cards beginning with 2131 or 1800 have 15 digits. JCB cards beginning with 35 have 16 digits.

      const visa_img_regex = /^4$/;
      const master_card_img_regex = /^(?:5[1-5]|222[1-9]|22[3-9][0-9]|2[3-6][0-9]{2}|27[01][0-9]|2720)$/;
      const amex_img_regex = /^3[47]$/;
      const diners_img_regex = /^3(?:0[0-5]|[68])$/;
      const discover_img_regex = /^6(?:011|5)$/;
      const jcb_img_regex = /^(?:2131|1800|35)$/;

      if (visa_img_regex.test(cardNumber)) {
        this.cardImg = "visa_img";
      } else if (master_card_img_regex.test(cardNumber)) {
        this.cardImg = "master_card_img";
      } else if (amex_img_regex.test(cardNumber)) {
        this.cardImg = "amex_img";
        this.cardLength = 17;
        this.cardMaskPattern = "#### ###### #####";
        this.cvvLength = 4;
      } else if (diners_img_regex.test(cardNumber)) {
        this.cardImg = "diners_img";
        this.cardLength = 14;
        this.cardMaskPattern = "#### ###### ####";
      } else if (discover_img_regex.test(cardNumber)) {
        this.cardImg = "discover_img";
      } else if (jcb_img_regex.test(cardNumber)) {
        this.cardImg = "jcb_img";
        if (/^(?:2131|1800)$/.test(cardNumber.substring(0, 4))) {
          this.cardLength = 15;
        }
      } else {
        if (cardNumber !== "") {
          this.cardImg = this.cardImg;
        } else {
          this.cardImg = "";
        }
      }
    },
    restrictChars: function($event) {
      if (
        $event.charCode === 0 ||
        /\d/.test(String.fromCharCode($event.charCode))
      ) {
        return true;
      } else {
        $event.preventDefault();
      }
    }
  }
};
</script>

